<?phpfunction __autoload($classname){	include_once("c/$classname.php");}//php2/framework/index.php/index.php?action=auth&control=user$action = 'action_';$action .= (isset($_GET['action'])) ? $_GET['action'] : 'index';switch ($_GET['control']){	case 'user':		$controller = new C_User();		break;	default:		$controller = new C_Index();}$controller->Request($action);