<?phpinclude_once('m/model.php');function __autoload($classname){    include_once("c/$classname.php");}//php2/framework/index.php/index.php?action=auth&control=user$action = 'action_';switch ($_REQUEST['control']) {    case 'user':        $controller = new C_User($_POST['login'], $_POST['password']);        if($controller->auth)            $action .= 'enter';        else            $action .= 'auth';        break;    default:    {        $action .= (isset($_GET['action'])) ? $_GET['action'] : 'index';        $controller = new C_Index();    }}$controller->Request($action);