<?phpconst LOGIN = 'admin';const PASSWORD = '123';const AUTH_NAME = '::Авторизация';const EXIT_NAME = '::Выход';function text_get(){    return file_get_contents('data/text.txt');}function text_set($text){    file_put_contents('data/text.txt', $text);}function template($fileName, $vars = []){    //twig 2.12.6-DEV    try {        require_once '../vendor/autoload.php';        $loader = new \Twig\Loader\FilesystemLoader('v');        $twig = new \Twig\Environment($loader);        $content = $twig->render($fileName, $vars);        return $content;    } catch (Exception $e) {        die ('ERROR: ' . $e->getMessage());    }}function authorisation($login, $password){    if($login == LOGIN && $password == PASSWORD)        return true;    else        return false;}function check_auth(){    $active_user = '';    if ($_COOKIE['active-user'])        $active_user = $_COOKIE['active-user'];    //если есть активный юзер и у него есть логин и пароль    if ($active_user && $_COOKIE["$active_user-login"] && $_COOKIE["$active_user-password"]){        $login = $_COOKIE["$active_user-login"];        $password = $_COOKIE["$active_user-password"];        if(!authorisation($login, $password)){            setcookie('active-user', '', time()+1, '/');            return false;        }        return true;    }}function set_cookies($login, $password){    setcookie("$login-login", $login, time()+3600*24*7, '/');    setcookie("$login-password", $password, time()+3600*24*7, '/');    setcookie('active-user', $login, time()+3600*24*7, '/');}